__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night wet-sensor -> living room on",
    "parent": 0,
    "comment": "When wet sensor becomes On at night set fast ramp and turn lights on; turn off when sensor clears.",
    "if": [
      { "==": { "device": "55 8 AB 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "1D C4 F6 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 28, "uom": "25", "precision": 0 } ] },
      { "device": "1D C4 F6 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "1D C4 F6 1", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "COS on wet sensor during night should immediately set a fast ramp (0.5s = enum 28) and turn lights to full; when sensor goes off, ensure lights off."
  }
}
__END_NUCORE_ROUTINE__
