__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night: Hall or TV DFON -> Laundry backlight on",
    "parent": 0,
    "comment": "If either Basement Hall or TV Viewing is physically turned on during overnight hours, enable Laundry Room backlight.",
    "if": [
      { "(": 1 },
        { "==": { "device": "1B 98 AB 4", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "1B 98 AB 5", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "1B 98 AB 3", "command": "BL", "parameters": [ { "name": "n/a", "value": 16, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use COC (DFON) from Basement Hall or TV Viewing during overnight schedule to trigger Laundry Room backlight via the BL accept command. Value 16 is within the device's 0-127 backlight range (uom 25)."
  }
}
__END_NUCORE_ROUTINE__
