__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 201,
    "name": "Tree DFON (evening) -> Snowman On + Inga BL 80%",
    "parent": 0,
    "comment": "Trigger when Tree is fast-on during evening hours",
    "if": [
      { "from": { "time":"17:00","to":{ "time":"23:00","day":0 } } },
      { "and": 1 },
      { "==": { "device":"14 80 E4 1", "control":"DFON" } }
    ],
    "then": [
      { "device":"14 82 E8 1", "command":"DON" },
      { "device":"14 81 4E 1", "command":"BL", "parameters":[{ "name":"n/a","value":80,"uom":"51","precision":2 }] }
    ],
    "else": [],
    "reasoning": "COC: The routine responds to a physical fast-on (DFON) on the Tree. A schedule bounds the action to evening hours. Then actions turn on Snowman Posse and set Inga backlight to 80% using the BL parameter range defined in the device structure."
  }
}
__END_NUCORE_ROUTINE__
