__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday evening: DFON triggers Bottle Cellar on and set dining backlight",
    "parent": 0,
    "comment": "If Dining Room Lamp L is fast-on pressed (DFON) on weekdays 18:00-23:00, turn on Bottle Cellar and set backlight to 50%.",
    "if": [
      { "(": 1 },
      { "==": { "device": "36 5E 45 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "21 12 1B 1", "command": "DON", "parameters": [] },
      { "device": "36 5E 45 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "Use the DFON COC from Dining Room Lamp L restricted to weekdays 18:00-23:00 so a single tap quickly lights the Bottle Cellar and sets the dining lamp backlight to a moderate level. Actions use existing DON and BL commands and the BL parameter uses uom id 51 as defined."
  }
}
__END_NUCORE_ROUTINE__
