__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday morning occupancy — set dining heat to 70F",
    "parent": 0,
    "comment": "When Carls Jr occupancy arrives on weekdays 07:00-09:00, switch Dining thermostat to Heat and set heat setpoint to 70°F.",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri","from":{ "time":"07:00","to":{ "time":"09:00" } } } },
      { "and": 1 },
      { "==": { "device":"n001_s416400127894","status":"GV1","value":1,"uom":"25","precision":0 } }
    ],
    "then": [
      { "device":"n001_t416496082899","command":"CLIMD","parameters":[{ "name":"n/a","value":1,"uom":"67","precision":0 }] },
      { "device":"n001_t416496082899","command":"CLISPH","parameters":[{ "name":"n/a","value":700,"uom":"17","precision":1 }] }
    ],
    "else": [],
    "reasoning": "Use weekly schedule AND occupancy COS so heating is enabled only when staff arrive during morning hours. Mode=Heat (value 1) and Heat Setpoint = 70°F (encoded as 700 with precision 1)."
  }
}
__END_NUCORE_ROUTINE__
