__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Kitchen High Temp Alert (>=85°F)",
    "parent": 0,
    "comment": "Monitor Kitchen forecast High Temp on weekdays during business hours.",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "18:00" } } } },
      { "and": 1 },
      { ">=": { "device": "n001_f416412733203", "status": "GV3", "value": 850, "uom": "17", "precision": 1 } }
    ],
    "then": [],
    "else": [],
    "reasoning": "Trigger when Kitchen forecast High Temp (GV3) is greater than or equal to 85.0 °F (value scaled to 850 with precision 1) during weekdays 09:00–18:00. No device accepts commands in the provided DEVICE STRUCTURE, so the routine contains no action steps; integrate a notification or HVAC control device with commands to perform actions."
  }
}
__END_NUCORE_ROUTINE__
