__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Enable weather & set humidification at sunrise if rain forecasted",
    "parent": 0,
    "comment": "At sunrise, when forecast or local weather indicates Rain, enable thermostat weather and set humidification to 55%.",
    "if": [
      { "(": 1 },
        { "==": { "device": "n001_f416456848794", "status": "GV9", "value": 6, "uom": "25", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "n001_w416456848794", "status": "GV9", "value": 6, "uom": "25", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "at": { "sunrise": 0 } }
    ],
    "then": [
      {
        "device": "n001_t416451520990",
        "command": "GV1",
        "parameters": [
          { "name": "n/a", "value": 55, "uom": "22", "precision": 1 }
        ]
      },
      {
        "device": "n001_t416451520990",
        "command": "GV9",
        "parameters": [
          { "name": "n/a", "value": 1, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger runs at sunrise only when either the Forecast device or the Weather device reports the Weather enum value 'Rain' (value 6). Then it sets the Kitchen humidification setpoint to 55% (GV1) and enables the thermostat's Weather reporting (GV9). Uses device IDs and enum values defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
