__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on fireplace when leak detected overnight",
    "parent": 0,
    "comment": "Enable fireplace lights if either leak sensor reports water during overnight hours",
    "if": [
      { "(": 1 },
        { ">=": { "device": "4E 1C D4 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { ">=": { "device": "4E 1C D4 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "51 51 CD 2", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COS from either leak-related sensor (ST goes to On = 100) combined with an overnight schedule to ensure lights come on only at night when a water event occurs; action uses the Fire Place Lights (Bottom) DON command to illuminate.">
  }
}
__END_NUCORE_ROUTINE__
