__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening backlight on Spare.Relay ON",
    "parent": 0,
    "comment": "When Spare.Relay.1 goes ON during 18:00-23:00 set IR Linc backlights to 80%, else 20%.",
    "if": [
      { "==": { "device": "29 3F 72 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "1B 74 15 0", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "22 7E 84 0", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "1B 74 15 0", "command": "BL", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] },
      { "device": "22 7E 84 0", "command": "BL", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "Uses COS (Spare.Relay.1 status becomes On) AND an evening time window. Then sets Backlight (BL) on both IR Linc devices using UOM 51; else sets a lower backlight. All commands and UOMs are defined in the device structure."
  }
}
__END_NUCORE_ROUTINE__
