__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Garage open at sunset -> enable relay and beep",
    "parent": 0,
    "comment": "When the Garage Double Door Sensor reports On at sunset, set relay on and beep; otherwise ensure relay is off.",
    "if": [
      { "==": { "device": "28 C8 CE 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "28 C8 CE 2", "command": "DON", "parameters": [] },
      { "device": "28 C8 CE 1", "command": "BEEP", "parameters": [] }
    ],
    "else": [
      { "device": "28 C8 CE 2", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "Trigger on the sensor reporting ON (COS). Constrain to sunset to match the user request. If true, activate the relay and beep the sensor; else turn relay off to keep system consistent."
  }
}
__END_NUCORE_ROUTINE__
