__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query door and low-bat when Dusk.Dawn motion at sunset",
    "parent": 0,
    "comment": "When Dusk.Dawn sensor goes On at sunset, poll door and low-bat sensor",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "21 D6 AB 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "32 AF 85 1", "command": "QUERY" },
      { "device": "21 D6 AB 3", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Combine a schedule (sunset) with a COS (Dusk.Dawn status == On) so the routine only triggers when motion occurs after dark. Then poll the door and low-battery sensors (QUERY) to refresh their states for any downstream automation."
  }
}
__END_NUCORE_ROUTINE__
