__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Palm Lights when Master Closet Disco turns on",
    "parent": 0,
    "comment": "Trigger on COC from ZY009_252N002",
    "if": [
      { "==": { "device": "ZY009_252N002", "control": "DON" } }
    ],
    "then": [
      { "device": "ZY027_001_1", "command": "DON" }
    ],
    "else": [],
    "reasoning": "Use a COC (change of control) event: when Master Closet Disco (ZY009_252N002) sends a DON control, immediately send DON to Palm Lights 1 (ZY027_001_1) which accepts DON. No parameters required."
  }
}
__END_NUCORE_ROUTINE__
