__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion triggers sensor queries",
    "parent": 0,
    "comment": "Query multilevel and software alarm when motion detected at night",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"ZY028_380","control":"DON" } }
    ],
    "then": [
      { "device":"ZY028_118","command":"QUERY","parameters":[] },
      { "device":"ZY028_175","command":"QUERY","parameters":[] }
    ],
    "else": [],
    "reasoning": "At night motion ON (DON) likely indicates activity; querying the multilevel sensor and the software alarm captures environment and fault state immediately after motion is detected."
  }
}
__END_NUCORE_ROUTINE__
