__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Kitchen Island: weekday/switch -> set ramp and backlight",
    "parent": 0,
    "comment": "Set ramp to 1.0 minute and backlight to 50% when manually fast-on or during weekday morning window.",
    "if": [
      { "(": 1 },
        { "==": { "device": "4F A0 9A 1", "control": "DFON" } },
        { "or": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "11:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "4F A0 9A 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "4F A0 9A 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Respond to either physical Fast On (COC DFON) or a weekday schedule window. When triggered, set Ramp Rate to the enumerated value for 1.0 minute (value 12, uom 25) and set Backlight to 50% (uom 51). Kept minimal: no else actions here since off-handling is a separate COS routine."
  }
}
__END_NUCORE_ROUTINE__
__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Kitchen Island: when turned off -> backlight 0",
    "parent": 0,
    "comment": "When the island's Status becomes Off, clear the backlight.",
    "if": [
      { "==": { "device": "4F A0 9A 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "4F A0 9A 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "This COS ensures when the Kitchen Island reports Status Off (ST = 0, uom 51) the Backlight is set to 0% to conserve power and reflect off state."
  }
}
__END_NUCORE_ROUTINE__*/
