__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Patio fan motor to Medium when light manually turned on in evening",
    "parent": 0,
    "comment": "COC on Patio Fan-Light (DON) during evening hours -> set motor to Medium",
    "if": [
      { "==": { "device": "70 91 AC 1", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "70 91 AC 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 75, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on a Change of Control (manual ON = DON) for Patio Fan-Light during the specified time window; action sends an On command to the fan motor with the enumerated Medium value (75, uom 51)."
  }
}
__END_NUCORE_ROUTINE__
