__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night - Garage door opened -> beep main sensor",
    "parent": 0,
    "comment": "When the closed sensor reports open overnight, alert via main sensor beep.",
    "if": [
      { "from": { "time":"22:00", "to": { "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"37 DA CC 2","status":"ST","value":100,"uom":"51","precision":0 } }
    ],
    "then": [
      { "device":"37 DA CC 1","command":"BEEP","parameters":[] }
    ],
    "else": [],
    "reasoning": "Detects COS on the closed-sensor status to On during the overnight schedule; uses the main sensor's BEEP accept command to produce an audible alert. Precision 0 for enum-based status and uom 51 as defined in device structure."
  }
}
__END_NUCORE_ROUTINE__
