__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Button N press during work hours triggers O Pressed",
    "parent": 0,
    "comment": "When IR Button N is pressed Mon-Fri 09:00-17:00, send Pressed on Button O",
    "if": [
      { "weekly": { "days":"mon,tue,wed,thu,fri","from":{ "time":"09:00","to":{ "time":"17:00" } } } },
      { "and": 1 },
      { "==": { "device":"n009_irbutton_111","control":"Pressed" } }
    ],
    "then": [
      { "device":"n009_irbutton_112","command":"Pressed","parameters":[] }
    ],
    "else": [],
    "reasoning": "Use a COC on Button N (Pressed) combined with a weekly schedule Mon-Fri 09:00-17:00 to trigger Button O's Pressed send-command. This keeps logic simple and explicit."
  }
}
__END_NUCORE_ROUTINE__
