__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "BedsideL DFON -> set backlight 26",
    "parent": 0,
    "comment": "Set backlight when BedsideL is fast-on pressed",
    "if": [
      { "==": { "device": "70 12 D0 4", "control": "DFON" } }
    ],
    "then": [
      { "device": "70 12 D0 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 26, "uom": "25", "precision": 0 } ] },
      { "device": "70 12 D0 4", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "A COC (DFON) on BedsideL indicates a user control action; respond immediately by adjusting the button backlight to value 26 and persist with WDU so the setting is saved. Uses only commands accepted by the device (BL, WDU)."
  }
}
__END_NUCORE_ROUTINE__
