__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset DFON -> beep low-bat, else query tamper",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "31 8F 8B 2", "control": "DFON" } }
    ],
    "then": [
      { "device": "31 8F 8B 3", "command": "BEEP", "parameters": [] }
    ],
    "else": [
      { "device": "ZY020_157", "command": "QUERY", "parameters": [] }
    ],
    "reasoning": "Detect the fast-on (DFON) event from the dusk/dawn sensor at sunset (COC). If it occurs, notify the low-battery motion device via its BEEP command (accepted). If not, poll the tamper alarm to ensure no tamper event is present."
  }
}
__END_NUCORE_ROUTINE__
