__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night DFON press: query and write (garage)",
    "parent": 0,
    "comment": "If DFON (fast on) is pressed overnight, query and write changes; otherwise only query.",
    "if": [
      { "==": { "device": "37 94 33 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"37 94 33 1","command":"QUERY","parameters":[] },
      { "device":"37 94 33 1","command":"WDU","parameters":[] }
    ],
    "else": [
      { "device":"37 94 33 1","command":"QUERY","parameters":[] }
    ],
    "reasoning": "Detect the physical fast-on control (COC) from the garage door. When it occurs during the overnight window (22:00-06:00), perform a QUERY to get state and then WDU to apply any necessary write; if outside the window, only QUERY to inspect state. Uses DFON control and schedule to limit action to night hours."
  }
}
__END_NUCORE_ROUTINE__
