__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Beep PumpRoom-Dry when PumpRoom-Wet turns On during weekdays",
    "parent": 0,
    "comment": "Notify Dry pump when Wet pump turns on during business hours",
    "if": [
      { "==": { "device": "28 56 D 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "28 56 D 1", "command": "BEEP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use the PumpRoom-Wet Status property (ST==On) as the COS trigger and limit to weekdays 09:00-17:00 with a weekly schedule. The action uses PumpRoom-Dry's BEEP command which exists in its Accept Commands to notify during those hours."
  }
}
__END_NUCORE_ROUTINE__
