__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Xmas4 FastOn turns on Bistro and Xmas2",
    "parent": 0,
    "comment": "Link physical Fast On (DFON) on Xmas 4 to turning on two other devices",
    "if": [
      { "==": { "device": "49 3C FF 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "49 3B 55 1", "command": "DON", "parameters": [] },
      { "device": "37 37 95 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "A COC (DFON) on Xmas 4 indicates a user pressed Fast On; immediately send DON to Bistro Lights and Xmas 2 to synchronize lighting.">
  }
}
__END_NUCORE_ROUTINE__
