__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Motion after sunset keeps ZY172_199 awake 2h",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY172_380", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "ZY172_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY172_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 2, "uom": "20", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When motion is detected after sunset keep the Wake Up device awake and set its wake interval to 2 hours so it remains responsive during the event."
  }
}
__END_NUCORE_ROUTINE__
