__BEGIN_NUCORE_ROUTINE__
{"routine": {"id": 1, "name": "Weekday physical lock triggers fan on", "parent": 0, "comment": "When PORT-1 DFON during weekdays 08:00-18:00, turn fan on; otherwise turn fan off.", "if": [ { "==": { "device": "11 42 7 1", "control": "DFON" } }, { "and": 1 }, { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } } ], "then": [ { "device": "11 46 4F 1", "command": "DON" } ], "else": [ { "device": "11 46 4F 1", "command": "DOF" } ], "reasoning": "Use a COC (DFON) from PORT-1 combined with a weekday schedule to reliably control the fan. The fan accepts DON/DOF so issue those commands directly."}}
__END_NUCORE_ROUTINE__
