__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "RemoteLinc DFON after sunset - set deck lights and landing",
    "parent": 0,
    "comment": "Activate evening scene when RemoteLinc fast-on is pressed after sunset",
    "if": [
      { "==": { "device": "A 28 7A 5", "control": "DFON" } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "1D C4 1A 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "1D 43 BB 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC from Jennifer's RemoteLinc (DFON) and confirm sunset to ensure this scene only runs in the evening. Then set Deck Lights and Deck Landing using their DON commands with percent UOM 51."
  }
}
__END_NUCORE_ROUTINE__
