__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night flood response: open lights",
    "parent": 0,
    "comment": "When backyard flood sensor reports On overnight, power entry and living room lamp",
    "if": [
      { "==": { "device": "1D 58 AE 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "1D F3 CD 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "3E 4E 99 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 75, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Act only when Backyard Floods changes to On (value 100) and it's the overnight period (22:00-06:00). Then fully enable the Kit. back door light and set living room lamp to a safe mid-high level so occupants can see. Commands and parameter UOMs use values from DEVICE STRUCTURE (uom 51 percent)."
  }
}
__END_NUCORE_ROUTINE__
