__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Kitchen Backlight at Sunset if Responding",
    "parent": 0,
    "comment": "Set Kitchen Backlight to 80% 5 minutes before sunset only when device is responding",
    "if": [
      { "at": { "sunset": -300 } },
      { "and": 1 },
      { "==": { "device": "21 D7 2E 4", "status": "ERR", "value": 0 } }
    ],
    "then": [
      {
        "device": "21 D7 2E 4",
        "command": "BL",
        "parameters": [
          { "name": "n/a", "value": 80, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger at 5 minutes before sunset to provide evening backlight. Verify Responding==True (value 0) to avoid sending commands to an offline device. Use BL with the device's defined parameter name 'n/a' and uom 51."
  }
}
__END_NUCORE_ROUTINE__
