__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening physical kitchen fast-on sets backlight",
    "parent": 0,
    "comment": "When user physically fast-ons the kitchen switch near sunset, set backlight and persist changes",
    "if": [
      { "at": { "sunset": -300 } },
      { "and": 1 },
      { "==": { "device": "51 71 4E 8", "control": "DFON" } }
    ],
    "then": [
      { "device": "51 71 4E 8", "command": "BL", "parameters": [ { "name": "n/a", "value": 100, "uom": "25", "precision": 0 } ] },
      { "device": "51 71 4E 8", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger is a physical Change-of-Control (DFON) limited to a time near sunset. Then sets the Backlight parameter to index 100 (valid range 0-127, uom 25) and calls Write Changes to persist the change. No else actions required."
  }
}
__END_NUCORE_ROUTINE__
