__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night physical-on sync to group",
    "parent": 0,
    "comment": "When either outlet is physically turned on overnight, turn on the group",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY012_001_1", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY012_002_1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY012_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "If either left or right outlet is physically turned on during the night window, enable the group so the group follows the manual on action. Uses COC for physical control and a nightly schedule."
  }
}
__END_NUCORE_ROUTINE__
