__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "BedFastOn -> Ceiling2 60% evening",
    "parent": 0,
    "comment": "Triggered by Bed Lights DFON during evening hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "20 BF 31 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "12 42 47 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 60, "uom": "51", "precision": 2 } ] },
      { "device": "12 42 47 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COC: user fast-on (DFON) on Bed Lights should immediately set Ceiling #2 brightness to 60% and apply the 1.0 minute ramp rate (enum value 12). Restrict with a weekly time window 18:00-23:00 to only run in the evening."
  }
}
__END_NUCORE_ROUTINE__
