__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "R pressed -> S pressed on weekdays 09-17",
    "parent": 0,
    "comment": "When IR Button R is pressed during weekdays 09:00-17:00, send Pressed to IR Button S",
    "if": [
      { "==": { "device": "n009_irbutton_115", "control": "Pressed" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "n009_irbutton_116", "command": "Pressed", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for IR Button R's Pressed control and restrict by a weekly schedule (weekdays 09:00-17:00). When triggered, send the Pressed command to IR Button S."
  }
}
__END_NUCORE_ROUTINE__
