    "comment": "Start Zone 8 for 30 minutes at 06:00 if enabled",
    "if": [
      { "at": { "time": "06:00" } },
      { "and": 1 },
      { "==": { "device": "n011_449160fc17908", "status": "GV0", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n011_449160fc17908", "command": "START", "parameters": [ { "name": "n/a", "value": 30, "uom": "45", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Schedule triggers at 06:00 daily; the enabled check (GV0 == 1) prevents attempts to start a disabled zone. Use Zone 8's START command with 30 minutes (uom 45, precision 1)."
  }
}
