__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening double-tap sets cans two and three to 80%",
    "parent": 0,
    "comment": "Double-tap Great Room Cans (2 taps) Mon-Fri 18:00-21:00 to set On Level of Cans Two and Three to 80%",
    "if": [
      { "(": 1 },
        { "==": { "device": "4C 2F 67 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "21:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "49 A2 C7 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "49 BC 57 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC double-tap on the primary fixture limited to weekday evening hours to intentionally set both secondary fixtures to a consistent 80% On Level; actions use OL command with percent UOM 51."
  }
}
__END_NUCORE_ROUTINE__
