__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Stephbath WET -> Query and Beep on COS",
    "parent": 0,
    "comment": "Trigger when WET sensor transitions to On and device is responding",
    "if": [
      { "==": { "device": "2D 62 1E 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "==": { "device": "2D 62 1E 2", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "2D 62 1E 2", "command": "Query" },
      { "device": "2D 62 1E 2", "command": "Beep" }
    ],
    "else": [],
    "reasoning": "This routine listens for a COS where the stephbath WET sensor reports On (value 100, ST). It verifies the device is responding (ERR == 0). On that COS it polls the sensor (Query) and issues a Beep using commands listed in the device's accepts list."
  }
}
__END_NUCORE_ROUTINE__
