      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "53 5C 2E 1", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "1D C9 4A 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Schedule at sunset combined with a status check ensures foyer lights only come on when the front porch light is currently off; uses DON with percent parameter (uom 51)."
  }
}
