__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekend entry quick-on moods",
    "parent": 0,
    "comment": "Activate a low-level scene when outside door DFON pressed on weekend evenings",
    "if": [
      { "weekly": { "days": "sat,sun", "from": { "time": "20:00", "to": { "time": "23:00" } } } },
      { "and": 1 },
      { "==": { "device": "1D C7 9D 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "1D C7 65 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 20, "uom": "51", "precision": 0 } ] },
      { "device": "1D C7 65 1", "command": "RR",  "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] },
      { "device": "1D CE C6 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 15, "uom": "51", "precision": 0 } ] },
      { "device": "1D CE C6 1", "command": "RR",  "parameters": [ { "name": "n/a", "value": 18, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When the outside door receives a fast-on (DFON) control during weekend evening hours, set sconces to 20% and soundpanels to 15% and apply the 30.0 second ramp (enum value 18) for smooth fade-in. This uses a COC trigger (DFON) combined with a weekly time window."
  }
}
__END_NUCORE_ROUTINE__
