__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Stop all audio on Stop press or nightly 23:00",
    "parent": 0,
    "comment": "Stops YouTube and Classical Radio when Stop - B is pressed (COC) or nightly at 23:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "34 FF 72 1", "control": "DFON" } },
        { "or": 1 },
        { "at": { "time": "23:00" } },
      { ")": 1 }
    ],
    "then": [
      { "device": "34 FF 72 2", "command": "DOF", "parameters": [] },
      { "device": "34 FF 72 3", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User wants both child audio devices turned off either when Stop - B is physically pressed (COC) or on a nightly schedule at 23:00. The routine triggers on either event and issues DOF to both child devices."
  }
}
__END_NUCORE_ROUTINE__
