__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Set Switch.Light backlight when turned on",
    "parent": 0,
    "comment": "When Switch.Light becomes ON at sunset, set backlight to 50%",
    "if": [
      { "==": { "device": "20 6C 3C 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      {
        "device": "20 6C 3C 1",
        "command": "BL",
        "parameters": [
          { "name": "n/a", "value": 50, "uom": "51", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use a COS on the Switch.Light status (== On) combined with the exact sunset time. When both occur, send the Backlight command with a 50% value (uom 51, range 0-100) using the device's BL accept parameter."
  }
}
__END_NUCORE_ROUTINE__
