__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening: On Fast L -> Set R level and ramp",
    "parent": 0,
    "comment": "When L is manually fast-on in the evening, configure R",
    "if": [
      { "==": { "device": "1D C FD 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "21:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "1A B1 AD 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 40, "uom": "51", "precision": 0 } ] },
      { "device": "1A B1 AD 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COC trigger: detect a manual Fast On (DFON) on Nook TV Outlet L during the specified evening window. Then send On Level (OL) to Outlet R at 40% using UOM 51 and set Ramp Rate (RR) to enum value 12 (1.0 minute) using UOM 25. Uses only commands and UOMs defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
