__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday physical ON triggers garage scene responder",
    "parent": 0,
    "comment": "When the bathroom switch is physically turned on during workday hours and the device is responding, send a QUERY to the scene responder.",
    "if": [
      { "==": { "device": "ZY052_1", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } },
      { "and": 1 },
      { "==": { "device": "ZY052_1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY058_259", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "This routine listens for a Change of Control (COC) on the bathroom switch (DON). It restricts triggering to weekdays 08:00-18:00 and only when the device reports Responding (ERR == 0). When conditions match it sends a QUERY command to the garage scene responder device (ZY058_259)."
  }
}
__END_NUCORE_ROUTINE__
