__BEGIN_NUCORE_ROUTINE__
{"routine": {"id":101,"name":"Weekday evening quick-on sets MBR level and ramp","parent":0,"comment":"When either lamp is manually turned on fast (COC) during weekday evenings, set MBR level and ramp.","if": [ {"(": 1}, {"==": {"device": "4E D 4B 1","control": "DFON"}}, {"or": 1}, {"==": {"device": "57 9F 83 1","control": "DON"}}, {")": 1}, {"and": 1}, {"weekly": {"days": "mon,tue,wed,thu,fri","from": {"time": "18:00","to": {"time": "23:00"}}}} ],"then": [ {"device": "4E D 4B 1","command": "OL","parameters": [{"name": "n/a","value": 60,"uom": "51","precision": 0}]}, {"device": "4E D 4B 1","command": "RR","parameters": [{"name": "n/a","value": 12,"uom": "25","precision": 0}]} ],"else": [],"reasoning": "COC triggers (DFON or DON) are grouped so a manual fast-on of either lamp during weekday evenings will set the MBR Pole Lamp to a comfortable level (60%) and apply a 1.0 minute ramp (enum value 12) for smooth fade."}}
__END_NUCORE_ROUTINE__
