__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night motion -> RemoteLinc-3 backlight 80%",
    "parent": 0,
    "comment": "When motion sensor status becomes On during night, set remote 3 backlight to 80%",
    "if": [
      { "from": { "time":"22:00", "to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"27 90 46 1", "status":"ST", "value":100, "uom":"51", "precision":0 } }
    ],
    "then": [
      { "device":"A 28 7A 3", "command":"BL", "parameters":[ { "name":"n/a", "value":80, "uom":"51", "precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Apply only at night window (22:00-06:00) and trigger on the Motion-Sensor ST == On COS; then set the RemoteLinc - 3 Backlight to 80% using the device's BL accept command with correct UOM and parameter range."
  }
}
__END_NUCORE_ROUTINE__
