__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Notify UD Mobile at sunset when NodeServer connects",
    "parent": 0,
    "comment": "Send UD Mobile alert when NodeServer Online changes to Connected at sunset",
    "if": [
      { "==": { "device": "n001_controller", "status": "ST", "value": 1, "uom": "25", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      {
        "device": "n001_udmobile",
        "command": "GV10",
        "parameters": [
          { "name": "Group", "value": 0, "uom": "25", "precision": 0 },
          { "name": "Sound", "value": 5, "uom": "25", "precision": 0 },
          { "name": "Content", "value": 0, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger when NodeServer Online transitions to Connected and the current time is sunset; then send UD Mobile GV10 using the defined Sound enum (5 = Software Start Positive). Group and Content set to default numeric placeholders per device parameter enums."
  }
}
__END_NUCORE_ROUTINE__
