__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning: query party lights when master bath switches turned on",
    "parent": 0,
    "comment": "Query party lights when either master bath switch is physically turned on during weekday mornings.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY008_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY008_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "ZY020_252N002", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC triggers (physical ON: DON) from either master bath switch within a weekday morning time window to proactively refresh the party lights state via QUERY so downstream automations have current status."
  }
}
__END_NUCORE_ROUTINE__
