__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Enable preconditioning on hot weekday mornings",
    "parent": 0,
    "comment": "",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } },
      { "and": 1 },
      { ">=": { "device": "n040_climate9nf1241", "status": "ST", "value": 86, "uom": "17", "precision": 1 } }
    ],
    "then": [
      { "device": "n040_climate9nf1241", "command": "AUTOCON", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] },
      { "device": "n040_climate9nf1241", "command": "CABINTEMP", "parameters": [ { "name": "Driver side Temp", "value": 72, "uom": "17", "precision": 1 }, { "name": "Passenger side Temp", "value": 72, "uom": "17", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "During weekday mornings when people typically leave for work, if the cabin is hot (>= 86°F) enable preconditioning so the HVAC runs and set both driver and passenger target temperatures to 72°F to cool the vehicle before use. Uses AUTOCON (Enabled=1) and CABINTEMP parameters defined in the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
