__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Sunset warm plant light if off",
    "parent": 0,
    "comment": "15 minutes before sunset behavior for plant light",
    "if": [
      { "at": { "sunset": -900 } },
      { "and": 1 },
      { "==": { "device": "n001_88010e5119150b", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "n001_88010e5119150b", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 3000, "uom": "26", "precision": 1 }, { "name": "D", "value": 0, "uom": "42", "precision": 1 }, { "name": "BR", "value": 204, "uom": "100", "precision": 1 } ] }
    ],
    "else": [
      { "device": "n001_88010e5119150b", "command": "CLITEMP", "parameters": [ { "name": "n/a", "value": 3000, "uom": "26", "precision": 1 } ] }
    ],
    "reasoning": "Schedule at 15 minutes before sunset (sunset:-900). If the light is off (ST == 0) turn it on and set to 3000K at ~80% brightness (BR=204 mapped from 80% of 254). If it's already on, only adjust color temperature to avoid changing brightness/state. Immediate transition (D=0)."
  }
}
__END_NUCORE_ROUTINE__
