__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: LiFX Warm White and mobile notify",
    "parent": 0,
    "comment": "When bulbs exist at sunset, set Warm White and alert UD Mobile default group",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { ">=": { "device": "n012_lifxctl", "status": "GV0", "value": 1, "uom": "56", "precision": 1 } }
    ],
    "then": [
      { "device": "n012_lifxctl", "command": "SET_COLOR", "parameters": [ { "name": "n/a", "value": 10, "uom": "25", "precision": 0 } ] },
      { "device": "n004_udmobile", "command": "GV10", "parameters": [ { "name": "Group", "value": 0, "uom": "25", "precision": 0 }, { "name": "Sound", "value": 5, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "This routine waits for sunset and confirms at least one bulb is discovered (GV0 >= 1). If true, it sets all LiFX bulbs to Warm White (SET_COLOR value 10) and sends a UD Mobile GV10 message to the default group with the 'Software Start Positive' sound."
  }
}
__END_NUCORE_ROUTINE__
