__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday scene responder -> All On and set Ignore None",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY003_252N002", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "ZY003_183", "command": "ALLSET", "parameters": [ { "name": "n/a", "value": 255, "uom": "25", "precision": 0 } ] },
      { "device": "ZY003_183", "command": "ALLON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the basic assoc device sends On during weekday business hours, ensure Switch All ignores no commands and turn everything on to reflect scene activation."
  }
}
__END_NUCORE_ROUTINE__
