__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Workout start/stop via Fan-Light fast tap",
    "parent": 0,
    "comment": "Start workout on DFON: motor High + light ramp to 1.0m. Stop on DFOF: motor Off + lights Off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "51 CD 91 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "51 CD 91 1", "control": "DFOF" } },
      { ")": 1 }
    ],
    "then": [
      { "device": "51 CD 91 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "51 CD 91 2", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "This routine listens for a fast on (DFON) or fast off (DFOF) control on the Gym Fan-Light. When DFON occurs it sets the fan motor to High (DON parameter 100) and applies a 1.0 minute ramp rate to the lights (RR parameter value 12). The same trigger group includes DFOF so the automation can handle stop events via separate actions (see second routine)."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1002,
    "name": "Workout stop via Fan-Light fast off",
    "parent": 0,
    "comment": "When Gym Fan-Light sends DFOF, turn motor and lights off.",
    "if": [
      { "==": { "device": "51 CD 91 1", "control": "DFOF" } }
    ],
    "then": [
      { "device": "51 CD 91 2", "command": "DOF", "parameters": [] },
      { "device": "51 CD 91 1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "This separate routine ensures a clear action path for the Fast Off control (DFOF) from the Gym Fan-Light: turn fan motor off and lights off immediately. Commands used are DOF which require no parameters.")
  }
}
__END_NUCORE_ROUTINE__
