__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Fireplace-Manual-On -> Set 34 3D 8C 12 to 30% with fast ramp",
    "parent": 0,
    "comment": "When fireplce spot is manually turned on shortly after sunset, set device 34 3D 8C 12 to 30% and RR to 0.1s",
    "if": [
      { "==": { "device": "34 3D 8C 15", "control": "DFON" } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "sunset": 900 } } }
    ],
    "then": [
      { "device": "34 3D 8C 12", "command": "DON", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "34 3D 8C 12", "command": "RR", "parameters": [ { "name": "n/a", "value": 31, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on a manual Fast On (DFON) from the fireplace spot and restrict to the window shortly after sunset (sunset -> sunset+900s). Then immediately set device 34 3D 8C 12 to 30% (DON with 30% uom 51) and set its ramp rate to the 0.1s enum index (value 31, uom 25) so the change is effectively instant. No else actions required."
  }
}
__END_NUCORE_ROUTINE__
